def curve_3pool_rewards():
    """
    https://curve.readthedocs.io/dao-fees.html

    https://curve.readthedocs.io/dao-gauges.html?#calculating-claimable-rewards
    """
    curve_claim = w3.eth.contract(
        address=three_pool_gauge, abi=json.loads(three_pool_gauge_abi)
    )

    # Shows CRV earned tokens
    # gauge.claimable_reward(alice) - gauge.claimed_rewards_for(alice)
    earned_amount = (
        curve_claim.functions.claimable_reward(public_key).call()
        - curve_claim.functions.claimable_reward_for(public_key).call()
    )
    print(earned_amount)


